(11,	'ecomm-need-help-setting-up-your-store',	'info',	'en_US',	'Need help setting up your Store?',	'Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.',	'info',	'{}',	'pending',	'woocommerce.com',	'2020-10-21 20:04:11',	NULL,	0,	'plain',	'',	0),
(12,	'woocommerce-services',	'info',	'en_US',	'WooCommerce Shipping & Tax',	'WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.',	'info',	'{}',	'pending',	'woocommerce.com',	'2020-10-21 20:04:11',	NULL,	0,	'plain',	'',	0),
(13,	'ecomm-unique-shopping-experience',	'info',	'en_US',	'For a shopping experience as unique as your customers',	'Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.',	'info',	'{}',	'pending',	'woocommerce.com',	'2020-10-21 20:04:11',	NULL,	0,	'plain',	'',	0);
